import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import swal from 'sweetalert';

const Login = () => {
  const [user, setUser] = useState({
    email: '', password: ''
  })

  const handleChange = (e) => {
    const {name, value} = e.target;
    setUser({...user, [name]: value})
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    try {
      
    } catch (err) {
      swal({
        title: "Error",
        text: (err),
        icon: "error",
        button: "OK",
      });
    }
  }

  return (
    <div className="login-page">
      <form onSubmit={handleSubmit}>
        <input type="email" name="email" required placeholder="Email..." value={user.email} onChange={handleChange} />
        <input type="password" name="password" required placeholder="Password..." autoComplete="on" value={user.password} onChange={handleChange} />

        <div className="row">
          <button type="submit">Login</button>
          <Link to="/register">Register</Link>
        </div>
      </form>
    </div>
  )
}

export default Login
