import React, { useState, useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';

import { GlobalState } from '../../../GlobalState';

const OrderDetail = () => {
  const state = useContext(GlobalState);
  const [history] = state.userAPI.history;
  const [orderDetails, setOrderDetails] = useState([]);

  const params = useParams();

  useEffect(() => {
    if (params.id) {
      history.forEach(item => {
        if (item._id === params.id) setOrderDetails(item)
      })
    }
  }, [params, history])

  if (orderDetails.length === 0) return null;

  return (
    <div className="history-page">
      <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Address</th>
              <th>Postal Code</th>
              <th>Country Code</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>{orderDetails.address.recipient_name}</td>
              <td>{orderDetails.address.linel + " - " + orderDetails.address.city}</td>
              <td>{orderDetails.address.postal_code}</td>
              <td>{orderDetails.address.country_code}</td>
            </tr>
          </tbody>
        </table>

        <table>
          <thead>
            <tr>
              <th></th>
              <th>Products</th>
              <th>Quantity</th>
              <th>Price</th>
            </tr>
          </thead>
          <tbody>
            {
              orderDetails.cart.map(item => (
                <tr key={item._id}>
                  <td><img src={item.images.url} alt=""/></td>
                  <td>{item.title}</td>
                  <td>{item.quantity}</td>
                  <td>{item.price * item.quantity}</td>
                </tr>
              ))
            }
          </tbody>
        </table>

        <section className="recent">
            <div className="activity-payment">
              <div className="activity-card">

                  <div className="table-responsive">
                    <table>
                      <thead>
                        <tr>
                          <th>Order</th>
                          <th>Date Of Ordered</th>
                          <th>Address</th>
                          <th>Quantity</th>
                          <th>Total Money</th>
                        </tr>
                      </thead>
                      <tbody>
                        {
                          history.map(item => (
                            <tr key={item._id}>
                              <td className="td-team">
                                {
                                  item.cart.map(product => (
                                    <img src={product.images.url} alt="" className="img" key={product._id}/>
                                  ))
                                }
                              </td>
                              <td>{new Date(item.createdAt).toDateString()}</td>
                              <td>{item.address.recipient_name + " - " + item.address.line1 + " - " + item.address.city + " - " + item.address.country_code}</td>
                              <td>
                                {
                                  item.cart.map(product => (
                                    <p key={product._id}><span>{product.title + " - " + product.quantity}</span></p>
                                  ))
                                }
                              </td>
                              <td>
                                ${
                                  item.cart.reduce((pre, product) => pre + (product.quantity * product.price), 0)
                                }
                              </td>
                            </tr>
                          ))
                        }
                      </tbody>
                    </table>
                  </div>
              </div>
            </div>  
          </section>
    </div>
  )
}

export default OrderDetail
