import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import swal from 'sweetalert';
import axios from 'axios';

const Register = () => {
  const [user, setUser] = useState({
    email: '', password: ''
  })

  const handleChange = (e) => {
    const {name, value} = e.target;
    setUser({...user, [name]: value})
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    try {
      await axios.post('/user/login', {...user})

      localStorage.setItem('Login', true);
      window.location.href = "/"
    } catch (err) {
      swal({
        title: "Error",
        text: err.response.data.msg,
        icon: "error",
        button: "OK",
      });
    }
  }

  return (
    <div className="login-page">
      <form onSubmit={handleSubmit}>
        <input type="email" name="email" required placeholder="Email..." value={user.email} onChange={handleChange} />
        <input type="email" name="email" required placeholder="Email..." value={user.email} onChange={handleChange} />
        <input type="password" name="password" required placeholder="Password..." autoComplete="on" value={user.password} onChange={handleChange} />

        <div className="row">
          <button type="submit">Register</button>
          <Link to="/login">Login</Link>
        </div>
      </form>
    </div>
  )
}

export default Register
