import React, { useState, useContext } from 'react';
import { Link, useHistory } from 'react-router-dom';
import axios from 'axios';

import './header.css';
import { GlobalState } from '../../GlobalState';
import Menu from './icon/menu.svg';
import Close from './icon/close.svg';
import Cart from './icon/cart.svg';
import Logo from './icon/camera.png';

const Header = () => {
  const state = useContext(GlobalState);
  const [isLogged] = state.userAPI.isLogged;
  const [isAdmin] = state.userAPI.isAdmin;
  const [cart] = state.userAPI.cart;
  const [setSearch] = state.productsAPI.search;

  const [handleSearch, setHandleSearch] = useState('');
  const history = useHistory();

  const handleSubmit = (e) => {
    // e.preventDefault()
    history.push()
    setSearch(handleSearch)

  }

  const logoutUser = async () => {
    await axios.get('/user/logout')
    localStorage.removeItem('Login'); 
    window.location.href = '/';
  }

  const loggedRouter = () => {
    return (
      <>
        <li><Link to="/">User</Link></li>
        <li><Link to="/" onClick={logoutUser}>Logout</Link></li>
      </>
    )
  }

  return (
    <header>
      <div className="option_header">
        <div className="option_header-content">
          <div className="menu">
            <img src={Menu} alt="" width="30"/>
          </div>
          <div className="languages">
            <p>Vi-VN | En-US</p>
          </div>

          <ul>
            <li><Link to="/">Store Location</Link></li>
            <li><Link to="/">Blog</Link></li>
            { isAdmin 
              ? <li><Link to="/admin">My Admin</Link></li> 
              : <li><Link to="/">My wishlist</Link></li>
            }
            {
              isLogged 
              ? loggedRouter()
              : 
              <>
                <li><Link to="/login">Sign in</Link></li>
                <li><Link to="/register">Register</Link></li> 
              </>
            }
            <li>
              <img src={Close} alt="" width="30" className="menu" />
            </li>
          </ul>
        </div>
      </div>

      <div className="center_header">
        <img src={Logo} alt="" className="logo"/>
        <div className="search">
          <form onSubmit={handleSubmit}>
            <input type="text" name="search" id="search" placeholder="Search for products" onChange={(e) => setHandleSearch(e.target.value)}/>
            <button type="submit">Search</button>
          </form>
        </div>
        <div className="user">
          <Link to="/cart">
            <img src={Cart} alt="" width="20"/>
          </Link>
          <span>{cart.length}</span>
        </div>
      </div>

      <div className="navigation_header">
        <ul>
          <li><Link to="/">New arrivals</Link></li>
          <li><Link to="/">Brands</Link></li>
          <li><Link to="/">Women</Link></li>
          <li><Link to="/">Men</Link></li>
          <li><Link to="/">Other</Link></li>
          <li><Link to="/">Sale</Link></li>
        </ul>
      </div>
    </header>
  )
}

export default Header
