import React, { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

import './header.css';
import { GlobalState } from '../../GlobalState';
import Menu from './icon/menu.svg';
import Close from './icon/close.svg';
import Logo from './icon/camera.png';

const Header = () => {
  const state = useContext(GlobalState);
  const [categories] = state.categoryAPI.categories;
  const [isLogged] = state.userAPI.isLogged;
  const [isAdmin] = state.userAPI.isAdmin;
  const [cart] = state.userAPI.cart;
  const [search, setSearch] = state.productsAPI.search;

  const [handleSearch, setHandleSearch] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault()
    setSearch(handleSearch)
    setHandleSearch('')
  }

  const logoutUser = async () => {
    await axios.get('/user/logout')
    localStorage.removeItem('Login'); 
    window.location.href = '/';
  }

  const loggedRouter = () => {
    return (
      <>
        <li><Link to="/">User</Link></li>
        <li><Link to="/" onClick={logoutUser}>Logout</Link></li>
      </>
    )
  }

  return (
    <header>
      <div className="option_header">
        <div className="option_header-content">
          <div className="menu">
            <img src={Menu} alt="" width="30"/>
          </div>
          <div className="languages">
            <p>Vi-VN | En-US</p>
          </div>

          <ul>
            <li><Link to="/">Store Location</Link></li>
            <li><Link to="/">Blog</Link></li>
            { isAdmin 
              ? <li><Link to="/admin">My Admin</Link></li> 
              : <li><Link to="/">My wishlist</Link></li>
            }
            {
              isLogged 
              ? loggedRouter()
              : 
              <>
                <li><Link to="/login">Sign in</Link></li>
                <li><Link to="/register">Register</Link></li> 
              </>
            }
            <li>
              <img src={Close} alt="" width="30" className="menu" />
            </li>
          </ul>
        </div>
      </div>

      <div className="center_header">
        <div className="header">
          <img src={Logo} alt="" className="logo"/>

          <div className="menu_bars">
            <span><i className="fas fa-bars"></i> All Categories</span>
            <nav className="nav">
              <ul className="nav__list">
                {
                  categories.map(category => (
                    <Link to="/products"><li className="nav__item" key={category._id}>{category.name}</li></Link>
                  ))
                }
              </ul>
            </nav>
          </div>

          <div className="search">
            <form onSubmit={handleSubmit}>
              <input type="text" name="search" id="search" placeholder="Search for products" value={handleSearch} onChange={(e) => setHandleSearch(e.target.value.toLowerCase())}/>
              <button type="submit">Search</button>
            </form>
          </div>

          <div className="user">
            <i className="far fa-user"></i>
          </div>

          <div className="user-modal">
            <div className="modal-custom"></div>
          </div>

          <div className="user-cart">
            <Link to="/cart">
              <i className="fas fa-shopping-cart cart-number"></i>
            </Link>
            <span>{cart.length}</span>
          </div>
        </div>
      </div>
    </header>
  )
}

export default Header
